mapper.update:
update group_table
set is_completed=true
where group_id=#{groupId}

update group_table
set is_recruit=#{isRecruit}
where group_id=#{groupId}

update group_table
set group_name=#{groupName},
    group_description=#{groupDescription},
    group_aim=#{groupAim}
where group_id=#{groupId}
